INSERT INTO feriados (data, nome, tipo, descricao, uf, municipio) VALUES ('23/01/2015', 'Dia do Evangélico', 'ESTADUAL', 'Dia 23 de janeiro, é feriado no calendário oficial do Governo do Estado do Acre, conforme lei Nº 1538/2004. O Dia do Evangélico foi criado em 2004, por meio de projeto de lei apresentado pelo deputado Helder Paiva, aprovado pela então governador Jorge Viana', 'AC', '');
INSERT INTO feriados (data, nome, tipo, descricao, uf, municipio) VALUES ('08/03/2015', 'Dia da Mulher', 'ESTADUAL', 'Feriado conforme lei Nº 1411/2001, mas adiado para Sexta-Feira. O Dia Internacional da Mulher, celebrado em 8 de março, tem como origem as manifestações das mulheres russas por melhores condições de vida e trabalho e contra a entrada da Rússia czarista na Primeira1 Guerra Mundial.', 'AC', '');
