INSERT INTO feriados (data, nome, tipo, descricao, uf, municipio) VALUES ('29/06/2015', 'Dia de São Pedro', 'ESTADUAL', 'São Pedro foi um dos doze apóstolos de Jesus Cristo, segundo o Novo Testamento e, mais especificamente, os quatro Evangelhos. Os católicos consideram Pedro como o primeiro Bispo de Roma, sendo por isso o primeiro Papa da Igreja Católica.', 'AL', '');
