INSERT INTO feriados (data, nome, tipo, descricao, uf, municipio) VALUES ('25/03/2015', 'Data Magna do Ceará', 'ESTADUAL', 'É a data magna onde ocorrem as comemorações do estado do Ceará, sendo o mesmo dia da abolição da escravidão neste estado. O Ceará foi a primeira província brasileira a aboliar a escravidão.', 'CE', '');
