INSERT INTO feriados (data, nome, tipo, descricao, uf, municipio) VALUES ('11/08/2015', 'Dia de Santa Catarina', 'ESTADUAL', 'A Capitania de Santa Catarina foi criada em 11 de agosto de 1738. Em 28 de fevereiro de 1821 torna-se uma província, que viria a ser o atual estado de Santa Catarina com a Proclamação da República.', 'SC', '');
