INSERT INTO feriados (data, nome, tipo, descricao, uf, municipio) VALUES ('08/09/2015', 'Padroeira de Tocantins', 'ESTADUAL', 'O nascimento de Nossa Senhora ou a Natividade de Maria é uma festa litúrgica das Igrejas Católica e Anglicana, celebrada no dia 8 de setembro, nove meses após a sua Imaculada Conceição, celebrada em 8 de dezembro.', 'TO', '');
INSERT INTO feriados (data, nome, tipo, descricao, uf, municipio) VALUES ('05/10/2015', 'Aniversário de Tocantins', 'ESTADUAL', 'Em 5 de outubro de 1988, o norte de Goiás foi emancipado, passando a se chamar Tocantins. Em 1º de janeiro de 1989, a Unidade Federativa do Tocantins foi oficialmente instalada.', 'TO', '');
