INSERT INTO feriados (data, nome, tipo, descricao, uf, municipio) VALUES ('16/06/2022', 'Corpus Christi', 'FACULTATIVO', 'Ponto Facultativo no País, mas Feriado Municipal em algumas cidades.. Corpus Christi, expressão latina que significa Corpo de Cristo, é uma festa Cristã realizada na quinta-feira seguinte ao domingo da Santíssima Trindade.', '', '');
INSERT INTO feriados (data, nome, tipo, descricao, uf, municipio) VALUES ('15/10/2022', 'Dia do Professor', 'FACULTATIVO', 'FERIADO ESCOLAR - No dia 15 de outubro de 1827 (dia consagrado à educadora Santa Teresa de Ávila), o Imperador do Brasil, Pedro I, baixou um Decreto Imperial que criou o Ensino Elementar no Brasil. Pelo decreto, "todas as cidades, vilas e lugarejos teriam suas escolas de primeiras letras"', '', '');
INSERT INTO feriados (data, nome, tipo, descricao, uf, municipio) VALUES ('28/10/2022', 'Dia do Servidor Público', 'FACULTATIVO', 'O dia do servidor público é feriado apenas para funcionários públicos.', '', '');
INSERT INTO feriados (data, nome, tipo, descricao, uf, municipio) VALUES ('18/11/2022', 'Tratado de Petrópolis', 'FACULTATIVO', 'Feriado no dia 17de Novembro, segundo a lei Nº 57/1965, mas adiado para próxima Sexta-Feira. O atual estado brasileiro do Acre era, no início do século XX, uma região pertencente à Bolívia e foi incorporado ao Brasil pelo Barão do Rio Branco através do Tratado de Petrópolis, firmado em 17 de novembro de 1903.', '', '');
